import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function LoginForm() {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    otp: '' // ðŸ‘ˆ Added OTP field
  });
  const [showOTPField, setShowOTPField] = useState(false); // ðŸ‘ˆ New state
  const navigate = useNavigate();

  // Keep your original handleChange
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Modified handleSubmit for OTP flow
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!showOTPField) {
      // Step 1: Send OTP
      try {
        const response = await fetch('http://localhost:5000/api/send-otp', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email: formData.email })
        });
        
        if (response.ok) {
          alert('OTP sent to your email!');
          setShowOTPField(true); // ðŸ‘ˆ Show OTP field
        } else {
          alert('Failed to send OTP');
        }
      } catch (error) {
        console.error(error);
      }
    } else {
      // Step 2: Verify OTP
      try {
        const response = await fetch('http://localhost:5000/api/verify-otp', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            email: formData.email,
            otp: formData.otp
          })
        });

        if (response.ok) {
          navigate('/home'); // ðŸ‘ˆ Redirect on success
        } else {
          alert('Invalid OTP');
        }
      } catch (error) {
        console.error(error);
      }
    }
  };

  // Your original JSX (only add OTP field conditionally)
  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center px-4">
      <div className="bg-white p-8 rounded-lg w-full max-w-lg shadow-lg">
        {/* Keep all your original UI elements */}
        <h2 className="text-left mb-8 text-4xl font-extrabold text-gray-800">
          Login
        </h2>

        {/* Social Buttons (unchanged) */}
        <div className="flex gap-4 justify-center mb-6">
          <button onClick={() => console.log('Google login')}>
            {/* Your Google button */}
          </button>
          <button onClick={() => console.log('Facebook login')}>
            {/* Your Facebook button */}
          </button>
        </div>

        <p className="text-center text-sm font-bold my-6">or</p>

        <form onSubmit={handleSubmit} className="flex flex-col">
          {/* Your original email/password fields */}
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="p-3 mb-4 rounded border border-gray-300"
            placeholder="Enter your email"
            required
          />
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            className="p-3 mb-4 rounded border border-gray-300"
            placeholder="Enter your password"
            required
          />

          {/* ðŸ‘‡ Only add this OTP field */}
          {showOTPField && (
            <input
              type="text"
              name="otp"
              value={formData.otp}
              onChange={handleChange}
              className="p-3 mb-4 rounded border border-gray-300"
              placeholder="Enter OTP"
              required
            />
          )}

          {/* Your original login button */}
          <button
            type="submit"
            className="p-2 h-12 bg-gray-800 text-white text-lg font-bold rounded cursor-pointer mt-3 hover:bg-purple-600"
          >
            {showOTPField ? 'Verify OTP' : 'Login'} {/* ðŸ‘ˆ Dynamic button text */}
          </button>
        </form>

        {/* Keep your "Forgot password?" and "Don't have an account?" links */}
        <p className="text-center mt-5">
          <a href="#" className="text-blue-600">Forgot password?</a>
        </p>
        <p className="text-center mt-2">
          Don't have an account? <a href="#" className="text-blue-600">Sign up</a>
        </p>
      </div>
    </div>
  );
}
