import React, { useState } from "react";
import { Link } from "react-router-dom";

function Login() {
  const [showPassword, setShowPassword] = useState(false);

  const togglePassword = () => setShowPassword((prev) => !prev);

  const handleSubmit = (e) => {
    e.preventDefault();
    // handle login logic here
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "flex-start", // align to top
        background: "#f4f4f4",
        padding: "2rem",
      }}
    >
      <div
        style={{
          background: "#fff",
          padding: "40px",
          width: "100%",
          maxWidth: "500px",
          border: "1px solid #ddd",
          borderRadius: "12px",
          marginTop: "40px",
          po
        }}
      >
        <img
          src="/rocket.png"
          alt="rocket"
          style={{
            position: "absolute",
            top: "10px",
            left: "10px",
            width: "50px",
          }}
        />

        {/* Logo + Title */}
        <div style={{ textAlign: "center", marginBottom: "30px" }}>
          <img
            src="/rocket.png"
            alt="rocket"
            style={{
              position: "absolute",
              top: "10px",
              left: "10px",
              width: "50px",
            }}
          />

          <h2
            style={{ marginTop: "25px", fontSize: "32px", fontWeight: "600" }}
          >
            Log in
          </h2>
        </div>

        {/* Login Form */}
        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: "20px" }}>
            <label
              htmlFor="email"
              style={{
                fontWeight: "600",
                fontSize: "18px",
                display: "block",
                marginBottom: "8px",
              }}
            >
              Email
            </label>
            <input
              type="email"
              id="email"
              placeholder="Enter your email"
              required
              style={{
                width: "100%",
                padding: "14px",
                fontSize: "16px",
                borderRadius: "6px",
                border: "1px solid #ccc",
              }}
            />
          </div>

          <div style={{ marginBottom: "20px", position: "relative" }}>
            <label
              htmlFor="password"
              style={{
                fontWeight: "600",
                fontSize: "18px",
                display: "block",
                marginBottom: "8px",
              }}
            >
              Password
            </label>
            <input
              type={showPassword ? "text" : "password"}
              id="password"
              placeholder="Enter your password"
              required
              style={{
                width: "100%",
                padding: "14px",
                paddingRight: "45px",
                fontSize: "16px",
                borderRadius: "6px",
                border: "1px solid #ccc",
              }}
            />
            <span
              onClick={togglePassword}
              style={{
                position: "absolute",
                top: "50%",
                right: "15px",
                transform: "translateY(-50%)",
                cursor: "pointer",
                fontSize: "16px",
                color: "#555",
              }}
              title={showPassword ? "Hide password" : "Show password"}
            >
              {showPassword ? "üôà" : "üëÅÔ∏è"}
            </span>
            <div style={{ textAlign: "right", marginTop: "8px" }}>
              <Link
                to="/forgot-password"
                style={{
                  fontSize: "15px",
                  color: "#007bff",
                  textDecoration: "none",
                }}
              >
                Forgot password?
              </Link>
            </div>
          </div>

          <button
            type="submit"
            style={{
              width: "100%",
              padding: "14px",
              backgroundColor: "#111",
              color: "#fff",
              fontSize: "18px",
              fontWeight: "bold",
              border: "none",
              borderRadius: "8px",
              cursor: "pointer",
              marginTop: "10px",
            }}
          >
            Log in
          </button>
        </form>

        <p style={{ textAlign: "center", marginTop: "25px", fontSize: "16px" }}>
          Don‚Äôt have an account?{" "}
          <Link
            to="/signup"
            style={{
              color: "#007bff",
              textDecoration: "none",
              fontWeight: "500",
            }}
          >
            Sign Up
          </Link>
        </p>
      </div>
    </div>
  );
}

export default Login;
