import React, { useState } from 'react';

export default function LoginForm() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Login submitted:', { email, password });
    // Add your login logic here
  };

  const handleSocialLogin = (provider) => {
    console.log(`${provider} login clicked`);
    // Add your social login logic here
  };

  const handleForgotPassword = (e) => {
    e.preventDefault();
    console.log('Forgot password clicked');
    // Add forgot password logic here
  };

  const handleSignUp = (e) => {
    e.preventDefault();
    console.log('Sign up clicked');
    // Add sign up navigation logic here
  };

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center px-4">
      <div className="bg-white p-5 rounded-lg w-full max-w-md shadow-lg">
        {/* Title */}
        <h1 className="text-left mb-8 text-3xl text-gray-800 font-bold">
          Log in
        </h1>

        {/* Social Buttons */}
        <div className="flex gap-4 justify-center mb-6">
          <button
            onClick={() => handleSocialLogin('Google')}
            className="flex items-center justify-center px-8 py-2 border-2 border-gray-300 rounded-md text-sm text-black bg-gray-100 min-w-[120px] h-10 transition-all duration-200 hover:bg-gray-200 hover:scale-105"
          >
            <img
              src="/google.png"
              alt="Google"
              className="w-6 h-6 mr-4"
            />
            <span className="font-bold text-base">Google</span>
          </button>

          <button
            onClick={() => handleSocialLogin('Facebook')}
            className="flex items-center justify-center px-8 py-2 border-2 border-gray-300 rounded-md text-sm text-black bg-gray-100 min-w-[120px] h-10 transition-all duration-200 hover:bg-gray-200 hover:scale-105"
          >
            <img
              src="https://cdn-icons-png.flaticon.com/512/5968/5968764.png"
              alt="Facebook"
              className="w-6 h-6 mr-4"
            />
            <span className="font-bold text-base">Facebook</span>
          </button>
        </div>

        {/* Divider */}
        <p className="text-center text-sm font-bold my-6">or</p>

        {/* Login Form */}
        <form onSubmit={handleSubmit} className="flex flex-col">
          {/* Email Field */}
          <label htmlFor="email" className="text-sm text-gray-600 font-bold mb-3 ml-2">
            Email
          </label>
          <input
            type="email"
            id="email"
            name="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="p-3 mb-4 rounded border border-gray-300 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            placeholder="Enter your email"
            required
          />

          {/* Password Section */}
          <div className="flex justify-between items-center mb-3">
            <label htmlFor="password" className="text-sm text-gray-600 font-bold ml-2">
              Password
            </label>
            <button
              onClick={handleForgotPassword}
              className="text-xs text-blue-600 no-underline hover:underline bg-transparent border-none cursor-pointer p-0"
            >
              Forgot password?
            </button>
          </div>
          
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="p-3 mb-4 rounded border border-gray-300 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            placeholder="Enter your password"
            required
          />

          {/* Login Button */}
          <button
            type="submit"
            className="p-3 h-12 bg-gray-800 text-white text-base border-none rounded cursor-pointer mt-3 transition-colors duration-300 ease-in-out hover:bg-purple-600"
          >
            Log In
          </button>
        </form>

        {/* Signup Link */}
        <p className="text-center mt-5 text-sm text-gray-600">
          Don't have an account?{' '}
          <button
            onClick={handleSignUp}
            className="text-blue-600 no-underline hover:underline bg-transparent border-none cursor-pointer p-0"
          >
            Sign Up
          </button>
        </p>
      </div>
    </div>
  );
}
