import React from 'react';

const Login = () => {
  const handleSocialLogin = (provider) => {
    console.log(`Logging in with ${provider}`);
    // Add your actual social login logic here
  };

  const handleForgotPassword = (e) => {
    e.preventDefault();
    console.log('Forgot password clicked');
    // Add your forgot password logic here
  };

  const handleSignUp = (e) => {
    e.preventDefault();
    console.log('Sign up clicked');
    // Add your sign up navigation logic here
  };

  return (
    <div className="min-h-screen bg-gray-100 flex justify-center items-center font-sans">
      <div className="bg-white p-5 rounded-xl shadow-md w-96">
        <h1 className="text-2xl font-bold text-gray-800 mb-8 text-left">Log in</h1>

        <div className="flex gap-4 justify-center">
          <button
            onClick={() => handleSocialLogin('google')}
            className="flex items-center justify-center px-8 py-2 border-2 border-gray-300 rounded-md text-black bg-white hover:bg-gray-100 hover:scale-[1.02] transition-all duration-200 min-w-[120px] h-10"
          >
            <img 
              src="https://cdn-icons-png.flaticon.com/512/2702/2702602.png" 
              alt="Google" 
              className="w-6 h-6 mr-4" 
            />
            <span className="font-bold text-base">Google</span>
          </button>
          
          <button
            onClick={() => handleSocialLogin('facebook')}
            className="flex items-center justify-center px-8 py-2 border-2 border-gray-300 rounded-md text-black bg-white hover:bg-gray-100 hover:scale-[1.02] transition-all duration-200 min-w-[120px] h-10"
          >
            <img 
              src="https://cdn-icons-png.flaticon.com/512/5968/5968764.png" 
              alt="Facebook" 
              className="w-6 h-6 mr-4" 
            />
            <span className="font-bold text-base">Facebook</span>
          </button>
        </div>

        <p className="text-center text-sm font-bold my-6">or</p>

        <form className="flex flex-col">
          <label htmlFor="email" className="text-sm font-bold text-gray-600 mb-2 ml-2">Email</label>
          <input 
            type="email" 
            id="email" 
            name="email" 
            className="px-3 py-2 mb-4 rounded-md border border-gray-300 text-sm" 
            placeholder="Enter your email" 
            required 
          />

          <div className="flex justify-between items-center">
            <label htmlFor="password" className="text-sm font-bold text-gray-600 mb-2 ml-2">Password</label>
            <button 
              onClick={handleForgotPassword}
              className="text-xs text-blue-600 hover:underline bg-transparent border-none cursor-pointer"
            >
              Forgot password?
            </button>
          </div>
          <input 
            type="password" 
            id="password" 
            name="password" 
            className="px-3 py-2 mb-4 rounded-md border border-gray-300 text-sm" 
            placeholder="Enter your password" 
            required 
          />

          <button 
            type="submit" 
            className="h-12 bg-gray-900 text-white text-base rounded-md mt-2 hover:bg-purple-600 transition-colors duration-300"
          >
            Log In
          </button>
        </form>

        <p className="text-center text-sm text-gray-600 mt-5">
          Don't have an account?
          <button 
            onClick={handleSignUp}
            className="text-blue-600 hover:underline ml-1 bg-transparent border-none cursor-pointer"
          >
            Sign Up
          </button>
        </p>
      </div>
    </div>
  );
};

export default Login;
