import React, { useState, useEffect } from 'react';
import './Signup.css'; // ⬅️ move all CSS into this file or paste inline using styled-components if needed

const Signup = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [showPassword, setShowPassword] = useState(false);
  const [successMsg, setSuccessMsg] = useState('');
  const [mode, setMode] = useState('signup'); // 'signup' or 'login'

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const togglePassword = () => setShowPassword(prev => !prev);

  const handleSignup = (e) => {
    e.preventDefault();

    const { email, password } = formData;
    if (!email || !password) return alert('Please fill in all fields');
    if (password.length < 6) return alert('Password must be at least 6 characters long');

    const btn = document.querySelector('.signup-btn');
    const originalText = btn.textContent;
    btn.textContent = 'Creating Account...';
    btn.disabled = true;

    setTimeout(() => {
      btn.textContent = originalText;
      btn.disabled = false;
      setSuccessMsg('Account created successfully! Welcome to SmallTalk2Me!');
      setFormData({ email: '', password: '' });
    }, 2000);
  };

  const handleOAuthSignup = (provider) => {
    const btn = document.querySelector(`.${provider}-btn`);
    const originalText = btn.innerHTML;
    btn.innerHTML = `<div style="width: 20px; height: 20px; border: 2px solid #ccc; border-top: 2px solid #667eea; border-radius: 50%; animation: spin 1s linear infinite;"></div> Connecting...`;

    const style = document.createElement('style');
    style.textContent = '@keyframes spin { 0% { transform: rotate(0deg); } 100% { transform: rotate(360deg); } }';
    document.head.appendChild(style);

    setTimeout(() => {
      btn.innerHTML = originalText;
      setSuccessMsg(`Successfully signed up with ${provider}!`);
    }, 2000);
  };

  useEffect(() => {
    if (successMsg) {
      const timeout = setTimeout(() => setSuccessMsg(''), 5000);
      return () => clearTimeout(timeout);
    }
  }, [successMsg]);

  const switchMode = () => setMode(prev => (prev === 'signup' ? 'login' : 'signup'));

  return (
    <div className="container">
      <div className="logo">
        <div className="rocket">🚀</div>
        <h1>&lt;&gt;SmallTalk2Me</h1>
      </div>

      <h2 className="title">{mode === "signup" ? "Sign Up" : "Log in"}</h2>

      {successMsg && <div className="success-message">{successMsg}</div>}

      <div className="oauth-buttons">
        <button
          className="oauth-btn google-btn"
          onClick={() => handleOAuthSignup("google")}
        >
          Google
        </button>
        <button
          className="oauth-btn facebook-btn"
          onClick={() => handleOAuthSignup("facebook")}
        >
          Facebook
        </button>
      </div>

      <div className="divider">
        <span>or</span>
      </div>

      <form onSubmit={handleSignup}>
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Password</label>
          <div className="input-container">
            <input
              type={showPassword ? "text" : "password"}
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            <button
              type="button"
              className="password-toggle"
              onClick={togglePassword}
            >
              {showPassword ? "🙈" : "👁️"}
            </button>
          </div>
          <div className="forgot-password">
            <button
              type="button"
              onClick={() => alert("Forgot password clicked")}
              className="link-button"
            >
              Forgot password?
            </button>
          </div>
        </div>

        <button type="submit" className="signup-btn">
          {mode === "signup" ? "Sign Up" : "Log in"}
        </button>
      </form>

      <div className="login-link">
        {mode === "signup" ? (
          <>
            Already have an account?{" "}
            <button onClick={switchMode} type="button" className="link-button">
              Log In
            </button>
          </>
        ) : (
          <>
            Don't have an account?{" "}
            <button onClick={switchMode} type="button" className="link-button">
              Sign Up
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default Signup;
