import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function SignupForm() {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    otp: ''
  });
  const [showOTPField, setShowOTPField] = useState(false);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSendOTP = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/send-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email: formData.email })
      });
      const data = await response.json();
      if (response.ok) {
        alert('OTP sent to your email!');
        setShowOTPField(true);
      } else {
        alert(data.error || 'Failed to send OTP');
      }
    } catch (error) {
      alert('Network error. Check console.');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleVerifyOTP = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          email: formData.email,
          otp: formData.otp,
          password: formData.password
        })
      });
      const data = await response.json();
      if (response.ok) {
        navigate('/home', { state: { email: formData.email } });
      } else {
        alert(data.error || 'OTP verification failed');
      }
    } catch (error) {
      alert('Network error. Check console.');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center px-4">
      <div className="bg-white p-8 rounded-lg w-full max-w-lg shadow-lg">
        <h2 className="text-left mb-8 text-4xl font-extrabold text-gray-800">Sign up</h2>
        
        <form onSubmit={showOTPField ? handleVerifyOTP : handleSendOTP}>
          <label className="block text-base font-bold text-gray-600 mb-2">Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="p-3 mb-4 rounded border border-gray-300 w-full"
            required
            disabled={showOTPField}
          />

          <label className="block text-base font-bold text-gray-600 mb-2">Password</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            className="p-3 mb-4 rounded border border-gray-300 w-full"
            required
            disabled={showOTPField}
          />

          {showOTPField && (
            <>
              <label className="block text-base font-bold text-gray-600 mb-2">OTP</label>
              <input
                type="text"
                name="otp"
                value={formData.otp}
                onChange={handleChange}
                className="p-3 mb-4 rounded border border-gray-300 w-full"
                placeholder="Enter 6-digit OTP"
                required
              />
            </>
          )}

          <button
            type="submit"
            className={`p-3 w-full rounded text-white font-bold ${loading ? 'bg-gray-500' : 'bg-blue-600 hover:bg-blue-700'}`}
            disabled={loading}
          >
            {loading ? 'Processing...' : showOTPField ? 'Verify OTP' : 'Send OTP'}
          </button>
        </form>
      </div>
    </div>
  );
}
